// 投稿詳細
Route::get('/posts/{postId}', function (Request $request) {
    return [
        "id" => 1,
        "title" => "タイトル",
        "images" => [
            [
                "base64" => "data:image/png;base64,",
                "isLike" => true,
                "likeCount" => 1
            ],
            [
                "base64" => "data:image/png;base64,",
                "isLike" => false,
                "likeCount" => 2
            ],
        ],
        "isBookmark" => true,
        "bookmarkCount" => 1,
        "tags" => [
            "タグ1",
            "タグ2",
        ],
        "date" => "2023-03-11T12:34:56+09:00"
    ];
});

